const serverName = toCamelCase(env.get('serverName'));
const haServer = global.get("homeassistant")[serverName];
if(!haServer) {
    node.error("Invalid HA server name");
    return;
}
const states = haServer.states;
const helpers = env.get("helpers");

helpers.forEach(h => {
    const entityId = `${h.type}.${h.id}`;
    if(!states[entityId]) {
        const {id, type, ...data} = h;
        const apiData = {
            entity: h,
            payload: { 
                data 
            }
        };
        apiData.payload.data.type = `${type}/create`;

        node.send(apiData);
        node.status({text: `Creating ${entityId}`});
    }
});

node.done();

function toCamelCase(str) {
    return str.replace(/(?:^\w|[A-Z]|\b\w|\s+)/g, (match, index) => {
        if (+match === 0) return '';
        return index === 0 ? match.toLowerCase() : match.toUpperCase();
    });
}

