function isToday(start, end) {
  if (typeof start === "string") {
    start = getHolidayDate(now, start)
  }
  if (typeof end === "string") {
    end = getHolidayDate(now, end)
  }
  if (end === undefined) return monthDay === start;

  return monthDay >= start && monthDay <= end;
}

function getHolidayDate(date, holiday) {
    holiday = holiday.toLowerCase();
    // dates are formatted as month, week, day of the week
    // names should be lowercase
    const holidays = {
        "martin luther king day": [0,2,1],
        "president's day": [1,2,1],
        "daylight savings time begins": [2,1,0],
        "mother's day": [4,1,0],
        "memorial day": [4,-1,1],
        "father's day": [5,2,0],
        "labor day": [8,0,1],
        "columbus day": [9,1,1],
        "daylight savings time ends": [10,0,0],
        "thanksgiving day": [10,3,4]
    };
    let holidayDate;
    if(holiday === "easter") {
        holidayDate = getEasterDate(date); 
    } else {
        const [month, week, day] = holidays[holiday];
        holidayDate = getDate(date.getFullYear(), month, week, day);
    }

    const monthDay = getMonthDay(holidayDate);

    return monthDay;
}

function getMonthDay(date) {
    const monthString = String(date.getMonth() + 1).padStart(2, "0");
    const dayString = String(date.getDate()).padStart(2, "0");
    const monthDay = Number(`${monthString}${dayString}`);

    return monthDay;
}

function getDate(year, month, week, day) {
    let firstDay = 1;
    if (week < 0) {
        month++;
        firstDay--;
    }
    const date = new Date(year, month, (week * 7) + firstDay);
    if (day < date.getDay()) {
        day += 7;
    }
    date.setDate(date.getDate() - date.getDay() + day);
    return date;
}

/**
 * Calculates Easter in the Gregorian/Western (Catholic and Protestant) calendar
 * based on the algorithm by Oudin (1940) from http://www.tondering.dk/claus/cal/easter.php
 * @returns {array} [int month, int day]
 */
function getEasterDate(date) {
  const year = date.getFullYear();
  const f = Math.floor,
    // Golden Number - 1
    G = year % 19,
    C = f(year / 100),
    // related to Epact
    H = (C - f(C / 4) - f((8 * C + 13) / 25) + 19 * G + 15) % 30,
    // number of days from 21 March to the Paschal full moon
    I = H - f(H / 28) * (1 - f(29 / (H + 1)) * f((21 - G) / 11)),
    // weekday for the Paschal full moon
    J = (year + f(year / 4) + I + 2 - C + f(C / 4)) % 7,
    // number of days from 21 March to the Sunday on or before the Paschal full moon
    L = I - J,
    month = 3 + f((L + 40) / 44),
    day = L + 28 - 31 * f(month / 4);

  return new Date(`${month}/${day}/${year}`);
}

function getRandomPreset(list) {
  const activePreset = states[entityId].attributes.preset;
  const presetName = idLookup[activePreset];
  const filteredList = list.filter((e) => e !== presetName);

  return filteredList[Math.floor(Math.random() * filteredList.length)];
}

