// see https://gram.js.org/tl/messages/SendMessage

let randomId = BigInt(Math.floor(Math.random() * 1e15));
if(msg.payload.message.className == "Message"){
    msg.payload = {
        api: 'messages',
        func: 'SendMessage',
        args: {
            peer: msg.payload.chat,
            message: msg.payload.message.message,
            randomId: randomId,
            noWebpage: true,
            noforwards: true,
            scheduleDate: 0,
            // sendAs: "username",
        }
    }
    return msg;
}
